import chai, {expect} from 'src/external/chai.js';

// swaps the keys and values of a dictionary
function swapKeysAndValue(dict) {
    var newDict = {};
    for (var key in dict) {
        newDict[dict[key]] = key;
    }
    return newDict;
}


 
describe('swapKeysAndValue', function() {
    it('swaps keys and values', function() {
        var dict = {a: 1, b: 2, c: 3};
        var swapped = swapKeysAndValue(dict);
        expect(swapped).to.deep.equal({1: 'a', 2: 'b', 3: 'c'});
    })

    it('looses keys that have the same value', function() {
        var dict = {a: 1, b: 2, c: 1};
        var swapped = swapKeysAndValue(dict);
        expect(swapped).to.deep.equal({1: 'c', 2: 'b'});
    });

    xit('fails when a value is not a valid key', function() {
        var dict = {a: 1, b: 2, c: {}};
        expect(function() {swapKeysAndValue(dict)}).to.throw(TypeError);
    })
      


});

